<template>
	<view :style="{borderRadius,width,height}" style="display:inline-block;">
		<!-- 占位图使用背景图的方式 -->
		<view :class="['content',over?'over':'',useCssLoading?'animation':'']" :style="{borderRadius,background:useCssLoading?'':`url(${loadSrc})`}">
			<!-- 加载成功 -->
			<image class="loadOver" @load="load" @click="handleView" @error="error" v-if="status==1" :src="src" :mode="mode" :style="{opacity:isShow?'1':'0',	borderRadius,transition: `opacity ${duration/1000}s ${effect}`}"></image>
			
			<!-- 加载失败 -->
			<image class="loadOver" v-if="status==2" :src="errorSrc" mode="scaleToFill" :style="{opacity:isShow?'1':'0',borderRadius,transition: `opacity ${duration/1000}s ${effect}`}"></image>
		</view>

    <!-- 预览图片区域 -->
    <view v-show="preview" @click="closeView" class="preview j-full-curbox ban-child">
      <view class="j-full-center">
        <image :src="previewImg" :style="{width:previewWidth}" mode="widthFix" alt="图片">
      </view>
    </view>
	</view>
</template>

<script>
let loadTimer = null;
export default {
  props:{
    // 图片缩放模式
    mode:{
      type: String,
      default: 'scaleToFill'
    },

    //图片圆角 必须带尺寸单位
    borderRadius: {
      type: String,
      default: '0'
    },

    //图片宽度
    width: {
      type: String,
      default: '100%',
      required: true
    },

    height: {
      type: String,
      default: '100%',
      required: true
    },

    //图片链接
    src: {
      type: String,
      default: '',
      required: true
    },

    //当图片加载过快(存在缓存)至少显示多少秒加载动画
    minTimeOut: {
      type: String || Number,
      default: '300'
    },

    //当图片到屏幕哪个位置的时候开始加载 单位px 可以是负数
    showDistance: {
      type: Object,
      default: () => {
        bottom: 20
      }
    },

    //是否用css渐变占位图,否则使用加载中图片作为占位图
    useCssLoading: {
      type: Boolean,
      default: false
    },

    //加载中图片
    loadSrc: {
      type: String,
      default: 'data:image/png;base64,'
    },

    //加载失败图片
    errorSrc: {
      type: String,
      default: 'data:image/png;base64,'
    },

    //过渡效果  linear / ease / ease-in / ease-out / ease-in-out
    effect: {
      type: String,
      default: 'linear'
    },
    
    //图片加载成功后的过渡时间 单位毫秒
    duration: {
      type: String || Number,
      default: '300'
    },

    // 预览图片宽度
    previewWidth:{
      typeof: String,
      default: "90vw"
    }
  },
  data () {
    return {
      //0加载中 1加载成功 2加载失败
      status: 0, 

      isShow: false,
      
      // 是否结束 css渐变占位图动画
      over: false,

      // 预览图片
      preview: false,

      // 预览图片地址
      previewImg: '',
    };
  },

  watch: {
    //如果图片成功显示了 关掉css动画
    isShow(newVal, oldVal) {
      if (!newVal) return
      setTimeout(() => {
        this.over = true
      }, this.duration + 1000)
    },
    //当链接变化重新加载
    src() {
      if (!this.isShow) return
      this.over = false
      this.status = 0
      this.isShow = false
      this.$nextTick(() => {
        this.status = 1
      })
    }
  },

  mounted () {
    let Observer = uni.createIntersectionObserver(this);
    let load = false;

    // 目标是否被用户看见
    Observer.relativeToViewport(this.showDistance).observe('.content', (res) => {
      if(res.intersectionRatio> 0){
        this.status = 1
        loadTimer = new Date().getTime()
        Observer.disconnect()
      }
    })
  },

  methods: {
    // 图片加载完成
    load() {
      if (this.minTimeOut == 0) {
        this.isShow = true
        return
      }

      let newTimer = new Date().getTime() - loadTimer;

      // 当图片加载过快(存在缓存)至少显示多少秒加载动画
      if (newTimer < this.minTimeOut) {
        setTimeout(() => {
          this.isShow = true
        }, this.minTimeOut - newTimer)
      } else {
        this.isShow = true
      }
    },

    // 图片加载错误
    error() {
      this.status = 2
			this.isShow = true
    },

    // 预览大图
    handleView() {
      if (this.src) {
        this.previewImg = this.src;
        this.preview = true;
      }
    },

    // 关闭大图
    closeView() {
      this.preview = false;
    },
  }
}
</script>
<style lang='scss' scoped>
  .content {
		width: 100%;
		height: 100%;
		background-size: 100% 100% !important;
	}

  .animation {
		background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
		background-size: 400% 400% !important;
		animation: gradientBG 5s ease infinite;

		@keyframes gradientBG {
			0% {
				background-position: 0% 50%;
			}

			50% {
				background-position: 100% 50%;
			}

			100% {
				background-position: 0% 50%;
			}
		}
	}

	.over {
		background: rgba(0, 0, 0, 0);
		animation: none;
	}

  .loadOver {
		opacity: 0;
		width: 100%;
		height: 100%;
		display: block;
		transform: transition3d(0, 0, 0);
		will-change: transform;
	}

  .preview {
    position: fixed;
    z-index: 999;
    background: rgba(0, 0, 0, 0.6);
  }
</style>